Bash(node -e "
const { createClient } = require(''@supabase/supabase-js'');
const fs = require(''fs'');
const path = require(''path'');
const envPath = path.join(process.cwd(), ''.env.local'');
const envFile = fs.readFileSync(envPath, ''utf-8'');
const envVars = {};
envFile.split(''\n'').forEach(line => {
  const match = line.match(/^([^=]+)=(.*)$/);
  if (match) envVars[match[1].trim()] = match[2].trim();
});
const supabase = createClient(envVars.NEXT_PUBLIC_SUPABASE_URL, envVars.SUPABASE_SERVICE_ROLE_KEY || envVars.NEXT_PUBLIC_SUPABASE_ANON_KEY);
(async () => {
  const { data } = await supabase.from(''categories'').select(''name, slug'').eq(''is_active'', true).order(''name'');
  console.log(JSON.stringify(data, null, 2));
})();
")
